script:
const domainName = window.location.origin;
var register_payload = {}

function getRegisterInput(event) {
    register_payload[event.name] = event.value;
}

function validatePassword(password) {
    var uppercaseRegex = /[A-Z]/;
    var lowercaseRegex = /[a-z]/;
    var numberRegex = /[0-9]/;
    var symbolRegex = /[$@$!%*?&/]/;  
    return password.length >= 8 &&
        uppercaseRegex.test(password) &&
        lowercaseRegex.test(password) &&
        numberRegex.test(password) &&
        symbolRegex.test(password);
}

function validateEmail(email) {    
    var emailRegex = /\S+@\S+\.\S+/;
    return emailRegex.test(email);
}

function registerError(message) {
    var existingError = document.querySelector('.reg-error-message');
    if (existingError) {
        existingError.remove();
    }
    var errorMessage = document.createElement('p');
    errorMessage.textContent = message;
    errorMessage.classList.add('reg-error-message');
    errorMessage.style.color = 'red';
    errorMessage.style.fontSize = '12px';

    var pswdContainer = document.querySelector(".password-input-field");
    pswdContainer.parentNode.insertBefore(errorMessage, pswdContainer.nextSibling);
}

function clearRegErrorMessages() {
    
    var errorMessages = document.querySelectorAll('.reg-error-message');
    errorMessages.forEach(function(errorMessage) {
        errorMessage.remove();
    });
}

document.querySelector(".full-name-input-field").addEventListener('click', clearRegErrorMessages);
document.querySelector(".email-input-field").addEventListener('click', clearRegErrorMessages);
document.querySelector(".phone-input-field").addEventListener('click', clearRegErrorMessages);
document.querySelector(".password-input-field").addEventListener('click', clearRegErrorMessages);

async function register(event) {
    const fullName = document.querySelector(".full-name-input-field").value;
    const email = document.querySelector(".email-input-field").value;
    const phone = document.querySelector(".phone-input-field").value;
    const password = document.querySelector(".password-input-field").value;
    clearRegErrorMessages();

    if (!fullName || !email || !phone || !password) {
        registerError('Please enter all the fields!');
        return; 
    }

    if (!validateEmail(email)) {
        registerError('Please enter a valid email address!');
        return;
    }

    if (!validatePassword(password)) {
        registerError('Password must contain at least one uppercase letter, one lowercase letter, one number, one symbol, and be at least 8 characters long!');
        return; 
    }

    console.log("register_payload", { data: register_payload });
    let res = await postMethod("go1_webshop.go1_webshop.api.insert_theme_register", {
        "full_name": fullName,
        "email": email,
        "phone": phone,
        "password": password 
    });

    if (res && res.message && res.message === 'Success') {
        console.log(res.message);
        window.location.href = domainName + '/browse-go1-themes';
    }
}

async function postMethod(api, payload) {
    let k = await fetch(domainName + "/api/method/" + api, {
        "body": JSON.stringify(payload),
        "method": "POST",
        "headers": {
            "Content-Type": "application/json",
            "x-frappe-csrf-token": frappe.csrf_token
        }
    });
    let val = await k.json();
    return val;
}

const domainName = window.location.origin;

function collapse(event) {
    let themesElement = event.parentNode.querySelector('.themes');
    if (themesElement.style.display === 'block' || themesElement.style.display === '') {
        themesElement.style.display = 'none';
    } else {
        themesElement.style.display = 'block';
    }
}


function setImage(event) {
    // $('.detail_theme_image').attr('src', event.querySelector('img').src);
    document.querySelector(".detail_theme_image").src = event.querySelector('img').src;
    // document.querySelector(".image_title").textContent = event.querySelector(".category_title").textContent;
}

async function getTemplate(event, data) {
    document.querySelector(".loading").style.display = "flex"
    const currentUrl = window.location.href;
    const lastSegment = currentUrl.split('/').pop();
    let res = await postMethod("go1_webshop.go1_webshop.after_install.insert_pages", {
        theme: lastSegment,
        nodata: data
    });
    if (res && res.message) {
        window.location.href = "/";
    }
}

async function postMethod(api, payload) {
    let k = await fetch(domainName + "/api/method/" + api, {
        "body": JSON.stringify(payload),
        "method": "POST",
        "headers": {
            "Content-Type": "application/json",
            "x-frappe-csrf-token":frappe.csrf_token
        }
    });
    let val = await k.json();
    return val;
}
