/* Cart Animation*/

@keyframes right-cart {
  0% {
        transform: translate(100%, 0);
    opacity: 0;}
     80% {
        transform: translate(-5%, 0);
    opacity: 0.8;}
  100% {
      transform: translate(0%, 0);
    opacity: 1;}
}
@keyframes mobile-right-cart {
  0% {
        transform: translate(0,100%);
    opacity: 0;}
  100% {
      transform: translate(0,0%);
    opacity: 1;}
}

