       string += `sort_by=`+sort_by+'&'
    }
    if(start && key != 'start'){
       string += `start=`+start+'&'
    }
    let params = string ? '?'+string+`${key}=`+value : `?${key}=`+value;
     window.location = (domain_name+path+params)
