function checkAttributeValues(){
    const allAttributes = document.querySelectorAll('[data-key="attribute"]')
    console.log("allAttributes",allAttributes)
    for (let attribute of allAttributes) {
        let variants = attribute.querySelectorAll('[data-key="variants"]')
         for (let variant of variants) {
             if(attributes[attribute["dataset"].value] && attributes[attribute["dataset"].value].includes(variant.dataset.value)){
                 attribute.querySelector(`[id="${variant.dataset.value}"]`).checked=true
             }
         }
    }
}

function checkAttribute(event){
    let attr_name = event.parentNode.parentNode.parentNode.parentNode.dataset.value
    let attr_value = event.id
    if(event.checked){
         if(attributes[attr_name]){
             attributes[attr_name].push(event.id)
         }else{
             attributes[attr_name]=[event.id]
         }
         
    }else{
        const index = attributes[attr_name].indexOf(event.id);
        if (index > -1) { 
          attributes[attr_name].splice(index, 1);
        }
        if(attributes[attr_name].length == 0){
            delete attributes[attr_name]
        }
    }
    loadPageWithParams('attributes',JSON.stringify(attributes))
}


    
function getCookie(cname) {
  let name = cname + "=";
  let decodedCookie = decodeURIComponent(document.cookie);
  let ca = decodedCookie.split(';');
  for(let i = 0; i <ca.length; i++) {
    let c = ca[i];
    while (c.charAt(0) == ' ') {
      c = c.substring(1);
    }
    if (c.indexOf(name) == 0) {
      return c.substring(name.length, c.length);
    }
  }
  return "";
}

