 function checkProductQuantity(){
  var cards = document.querySelectorAll('[field-name="product-card"]');
   for (let card of cards) {
        if(card.querySelector('#cart-count') && Math.round(card.querySelector('#cart-count').textContent) > 0){
            if(card.querySelector('[field-name="add-cart"]'))
            {
                card.querySelector('#cart-count').textContent = Math.round(card.querySelector('#cart-count').textContent);
                card.querySelector('[field-name="add-cart"]').classList.add("hide");
            card.querySelector('[field-name="add-cart"]').classList.remove("block");
                }
            if(card.querySelector('[field-name="update-cart"]')){
                card.querySelector('[field-name="update-cart"]').classList.add("block");
            card.querySelector('[field-name="update-cart"]').classList.remove("hide");
                
            }
        }else{
            if(card.querySelector('[field-name="add-cart"]')){
                card.querySelector('[field-name="add-cart"]').classList.add("block");
                card.querySelector('[field-name="add-cart"]').classList.remove("hide");
            }
            if(card.querySelector('[field-name="update-cart"]')){
                card.querySelector('[field-name="update-cart"]').classList.add("hide");
                card.querySelector('[field-name="update-cart"]').classList.remove("block");
            }
        }
    }
}


function getActiveCategory(){
      categories = document.querySelectorAll('[id="category-route"]');
    for (let cate of categories) {
        if(cate.textContent == path) cate.parentNode.parentNode.querySelector("#category").style.fontWeight=700
        if(cate.textContent == path && cate.parentNode.parentNode.querySelector("#category-child").hasChildNodes()){
            cate.parentNode.parentNode.querySelector("#category-child").classList.add("block")
        }
    }
}

function addToCart(event) {

    if (getCookie("user_id") != 'Guest') {
        var item_code = $(event).attr("item");
        var childText = event.parentNode.querySelector('#item').textContent;
        overlay = document.getElementById("overlay")
        overlay.classList.add("block")
        $.ajax({
            type: 'POST',
            Accept: 'application/json',
            ContentType: 'application/json;charset=utf-8',
            url: window.location.origin + "/api/method/webshop.webshop.shopping_cart.cart.update_cart",
            data: {
                "item_code": childText,
                "with_items": 0,
                "qty": 1,
            },
            dataType: "json",
            async: false,

            success: function(r) {
                overlay.classList.remove("block")
                showPopup("success", "Item added successfully");
                //  updateHeaderCartCount(parseInt($("#header-cart-count p").text())+1);
                overlay.classList.remove("block")
                let count_div = event.parentNode.querySelector("#cart-count");
                count_div.textContent = 1;
                updateHeaderCartCount(1);
                checkProductQuantity();
            }
        });

    } else {
        window.location = "/login"
    }

}
async function list_update_quantity(event,func){
    if(getCookie("user_id") != 'Guest'){
         let parent = event.parentNode.parentNode
    let cur_qty = parent.querySelector('#cart-count').textContent;
    if(func=='remove' && cur_qty == 0){
        showPopup("failed","Item Must Be Greater Than 0")
        return
    }
    let qty = func == 'add' ? Math.round(cur_qty) + 1 : Math.round(cur_qty) - 1
    let itemElement = parent.querySelector('[field-name="update-cart"]').getAttribute("data-item");
    let res = await updateCartItem(qty,itemElement)
    if (res){
        parent.querySelector('#cart-count').textContent = qty;
        updateHeaderCartCount(func == 'add' ? 1 : -1)
        showPopup("success","Cart Updated");
         checkProductQuantity();
    }
    }else{
        window.location = (domain_name+'/cart') 
    }
   
}

function updateHeaderCartCount(val){
    // console.log(val,"vval");
    let cur_head_qty = document.querySelector('#header-cart-count').textContent;
    document.querySelector('#header-cart-count').textContent = Math.round(cur_head_qty) + Math.round(val)
}
async function updateCartItem(qty,item_id){
    overlay = document.getElementById("overlay")
    overlay.classList.add("block")
    let k = await fetch(domain_name+"/api/method/webshop.webshop.shopping_cart.cart.update_cart",{
        "body":JSON.stringify({
            "with_items": 1,
            "qty": qty,
            "item_code": item_id
        }),
        "method":"POST",
        "headers":{
            "Content-Type":"application/json"
        }
    })
    let val = await k.json()
     overlay.classList.remove("block")
    return val.message ?  val.message : null;
}
 function updateHeaderCartCount(val){
    let cur_head_qty = document.querySelector('#header-cart-count').textContent;
    document.querySelector('#header-cart-count').textContent = Math.round(cur_head_qty) + Math.round(val)
}
function showPopup(func, message){
    console.log(func)
    var popup_ele = document.getElementById("popup")
    var content = document.getElementById("popup-content")
    content.innerHTML = `<p >${message}</p>`
    popup_ele.classList.add(func)
    setTimeout(()=>{ popup_ele.classList.remove(func) },2000)
}

