async function update_quantity(event,func){
    let parent = event.parentNode.parentNode.parentNode
     if(func != 'delete'){
                parent=parent.parentNode
            }
    let cur_qty = event.parentNode.querySelector('#qty').textContent;
    let qty = func == 'delete' ? 0 : func == 'add' ? Math.round(cur_qty) + 1 : Math.round(cur_qty) - 1
    let itemElement = parent.querySelector('#item_id').textContent;
    console.log(itemElement,qty)
    let res = await updateCartItem(qty,itemElement)
    if (res){
        event.parentNode.querySelector('#qty').textContent = qty;
        updateHeaderCartCount(func == 'delete' ? - Math.round(cur_qty) : func == 'add' ? 1 : -1)
        updateTotal(cur_qty,func,parent)
        showPopup('success',"Updated Seccessfully")
        if(qty==0){
            console.log(parent.id)
            if(parent.id && parent.id == 'item-info-div')  { parent.parentNode.remove() }
           else parent.remove();
           checkCartItems()
           setTimeout(()=>{enableCartButton()},3000)
        }
    }
}

function updateTotal(qty,func,elem){
    if(elem.querySelector('#sub-total') && elem.querySelector('#sub-total').textContent){
          let subtotal = Math.round(elem.querySelector('#sub-total').textContent.split('₹ ')[1].replace(/,/g, ""))
    let net_total = Math.round(document.querySelector('#net-total').textContent.split('₹ ')[1].replace(/,/g, ""))
    item_val = Math.round(subtotal)/Math.round(qty)
    console.log(item_val,"item_val")
    var subtotal_str = (parseFloat((func == 'delete' ? 0 : func == 'add' ? subtotal + Math.round(item_val) :subtotal - Math.round(item_val)),2)).toFixed(2).toString();
    elem.querySelector('#sub-total').textContent = '₹ '+format_money(subtotal_str)
    document.querySelector('#net-total').textContent =   '₹ '+format_money((parseFloat((func == 'delete' ? net_total - subtotal : func == 'add' ? net_total + Math.round(item_val) :  net_total - Math.round(item_val)),2)).toFixed(2).toString());
    document.querySelector('#grand-total').textContent = '₹ '+format_money((parseFloat((func == 'delete' ? net_total - subtotal :func == 'add' ? net_total + Math.round(item_val) : net_total - Math.round(item_val)),2)).toFixed(2).toString());
    $(".sub_total").text('₹ '+format_money((parseFloat((func == 'delete' ? net_total - subtotal :func == 'add' ? net_total + Math.round(item_val) : net_total - Math.round(item_val)),2)).toFixed(2).toString()));
    }
  
}

