function get_search_results() {
    if ($("#search_input").val().length >= 3) {
        $.ajax({
            type: 'POST',
            Accept: 'application/json',
            ContentType: 'application/json;charset=utf-8',
            url: window.location.origin,
            data: {
                "cmd": "webshop.templates.pages.product_search.search",
                "query": $("#search_input").val(),
            },
            dataType: "json",
            async: false,
            success: function(r) {
                if (r.message) {
                    var search_html = "";
                    if (r.message.category_results) {
                        if (r.message.category_results.length > 0) {
                            search_html += "<div class='search_categories'>"
                            search_html += "<div class='search_cat_title'>Categories</div>"
                            var categories = r.message.category_results;
                            for (var i = 0; i < categories.length; i++) {
                                search_html += "<a href='/products/" + categories[i].route + "' class='search_category'>" + categories[i].name + "</a>";
                            }
                            search_html += "</div>";
                        }
                    }
                    search_html += "<ul class='search_sets'>"
                    if (r.message.product_results) {
                        if (r.message.product_results.length > 0) {
                            var products = r.message.product_results;
                            for (var i = 0; i < products.length; i++) {
                                search_html += "<li><a  href='/product/" + products[i].route + "'>";
                                if (products[i].thumbnail) {
                                    search_html += "<img src='" + products[i].thumbnail + "' style='height:55px;width:55px'>";
                                } else {
                                    search_html += "<img src='/files/gf_no_image.png' style='height:55px;width:55px'>";
                                }
                                search_html += "<span>" + products[i].web_item_name + "</span>";
                                search_html += "</a></li>";
                            }

                        } else {
                            search_html += "<li class='no_search_data'>No Results Found.</li>";
                        }
                    } else {
                        search_html += "<li class='no_search_data'>No Results Found.</li>";
                    }
                    $(".search_results").html(search_html);
                    $(".search_results").show();
                }
            }
        });
    } else {
        $(".search_results").hide();
    }

}

function hide_search() {
    setTimeout(() => {
        $(".search_results").hide();
    }, 200)

}

