async function login() {
    var emailValue = document.querySelector(".eMail").value;
    var passwordValue = document.querySelector(".pswd").value;
    var checkMail = document.querySelector(".eMail").value.includes('@');

    clearErrorMessages();
    // console.log("login_payload",login_payload)
    if (!checkMail) {
        overlay.classList.remove("block");
        document.querySelector(".eMail").style.border = '1px solid red';
        displayErrorMessage('Please enter correct email!');
        return
    }
    show_popup();
    if (checkMail && !passwordValue) {
        overlay.classList.remove("block");
        crtMail();
    }
    console.log("entered Try")

    let res = await postMethod("login", {
        "cmd": "login",
        "usr": login_payload.username,
        "pwd": login_payload.password
    });


    if (res.message === 'Invalid login credentials') {
        overlay.classList.remove("block");
        validMessage();
        displayErrorMessage('Invalid email or password!');
    } else if (res && res.message) {

        if (emailValue && passwordValue) {
            overlay.classList.add("block"); // Show loading display
            if (window.location.search !== '') {
                loc = window.location.search.split('/')[1];
                window.location.href = (domainName + '/' + loc);
                console.log("entered loc")
            } else {
                window.location.href = (domainName + '/');
                console.log("entered out")
            }
        } else {
            overlay.classList.remove("block");
            validMessage();
            displayErrorMessage('Please enter valid email and password!');
        }
    }
}

