function remove_wish_list_item(e){
    if (getCookie("user_id") != 'Guest') {
        if (confirm("Are you sure want to remove?") == true) {
        var item_code = $(e).attr("item_code");
        overlay = document.getElementById("overlay")
        overlay.classList.add("block")
        $.ajax({
            type: 'POST',
            Accept: 'application/json',
            ContentType: 'application/json;charset=utf-8',
            url: window.location.origin,
            data: {
                "item_code": item_code,
                "cmd": "webshop.webshop.doctype.wishlist.wishlist.remove_from_wishlist"
            },
            dataType: "json",
            async: false,
            success: function(r) {
                overlay.classList.remove("block")
                showPopup("success", "Item removed successfully");
                overlay.classList.remove("block");
                document.querySelector(".w-count").textContent = parseInt(getCookie("wish_count"));
               $(e).closest(".related_item").remove();
            }
        });
        }

    } else {
        window.location = "/login"
    }
}

