function show_popup(){
    var div = document.createElement("div");
    div.className = 'popup-container'
    div.id = 'popup'
     div.innerHTML='<div class="popup-confirm"><div id="popup-content" class="popup-child1"><p >Text</p></div></div>'
      let overlay_div = document.createElement("div");
      overlay_div.innerHTML = '<div id="overlay"><span class="loader"></span></div>'
      console.log(div,overlay_div)
     document.body.appendChild(div);
     document.body.appendChild(overlay_div);
}
function displayPopup(func, message){
    var popup_ele = document.getElementById("popup")
    var content = document.getElementById("popup-content")
    content.innerHTML = `<p >${message}</p>`
    popup_ele.classList.add(func)
    setTimeout(()=>{ popup_ele.classList.remove(func) },3000)

}

async function delete_addresses(){
