async function get_country(){
    let res =  await postMethod("go1_builder.go1_builder.api.get_list",{doctype:"Country",page_size:260})
    for(let i of res.message){
        const country = document.getElementById('get-country');
        country.innerHTML += `<option value=`+i["name"]+`>` + i["name"] + `</option>`
        // console.log("Country...............",i["name"])

    }

    // message_popup(res)
