async function user_name(){
    let res = await postMethod("go1_webshop.go1_webshop.api.get_user",{doc:userFieldList})
    console.log("response", res)
    document.getElementsByClassName('userInput')[0].value = res.message[0].first_name
    document.getElementsByClassName('lastName')[0].value = res.message[0].last_name
    document.getElementsByClassName('mobile')[0].value = res.message[0].mobile_no
    document.getElementsByClassName('email')[0].value = res.message[0].email
}

var saveButton = document.getElementsByClassName('save_btn')[0];
var firstNameInput = document.getElementsByClassName('userInput')[0];
var lastNameInput = document.getElementsByClassName('lastName')[0];

firstNameInput.addEventListener('keypress', function(event) {
    if (event.key === 'Enter') {
        event.preventDefault(); 
        saveButton.click();
    }
});
lastNameInput.addEventListener('keypress', function(event) {
    if (event.key === 'Enter') {
        event.preventDefault();
        saveButton.click();
    }
});


saveButton.addEventListener('click', function() {
    console.log("Save BTN Clicked")
    userData['first_name'] = document.getElementsByClassName('userInput')[0].value;
    userData['last_Name'] = document.getElementsByClassName('lastName')[0].value;
    showPopup("success", "Saved")
    updateUserData();

});

