    let res = await postMethod("go1_webshop.go1_webshop.api.update_user", { doc: userData });
    console.log(res);
}    
function update_user(event){
    userData[event.name] = event.values
}

document.addEventListener('DOMContentLoaded', function () {
    const menuLinks = document.querySelectorAll('.menu-content a');
    menuLinks.forEach(link => {
        link.addEventListener('click', function(event) {
            event.preventDefault();
            const menuName = link.textContent.toLowerCase();
            routeMenu(menuName);
        });
    });

    function routeMenu(menuName) {
        switch (menuName) {
            case 'dashboard':
                navigateTo('/dashboard');
                break;
            case 'my profile':
                navigateTo('/profile');
                break;
            case 'my address':
                navigateTo('/address-list');
                break;
            case 'my order':
                navigateTo('/order-list');
                break;
            default:
                break;
        }
    }

    function navigateTo(url) {
        window.location.href = url;
    }

    const domainName = window.location.origin;
    console.log("domainName", domainName);
    var orderLinks = document.querySelectorAll('.order_list');
    orderLinks.forEach(function(link) {
        link.addEventListener('click', function(event) {
            event.preventDefault();
            var orderId = this.querySelector('.order_id').textContent;
            console.log("orderId", orderId);
            navigateTo(domainName + '/order-detail/' + orderId);
        });
    });
    
});

