let color_list = [
            '#FF6138',
            '#FF4131',
            '#A760FF',
            '#F94892',
            '#FEBD00',
            '#0086F9',
            '#BE6DB7',
            '#00AA4A',
            '#B85C38',
          ];

const parentDivs = document.querySelector('.main_div');

const divElementsInsideMainDiv = parentDivs.querySelectorAll('div');

divElementsInsideMainDiv.forEach(function(divElement) {
    // console.log("divElement",divElement)
    if (divElement.classList.contains('status')) {
        let status_value = divElement.textContent.trim()
        // console.log(status_value,"status_value")
        if(status_value == "Paid"){
            divElement.style.color = "#fff"
            divElement.style.backgroundColor = "green"
        }else if(status_value == "Completed"){
            divElement.style.color = "#fff"
            divElement.style.backgroundColor = "green"
        }else if(status_value == "Rejected"){
            divElement.style.color = "#fff"
            divElement.style.backgroundColor = "red"
        }else if(status_value == "Pending"){
            divElement.style.color = "#fff"
            divElement.style.backgroundColor = "orange"
        }
    }else if (divElement.classList.contains('avatar_color')) {
        console.log(divElement)

        for(let i = 0; i < color_list.length;i++){
             divElement.style.backgroundColor = color_list[i]
        }
        // color_list.forEach(function(color) {
        //     // console.log(color)
        //     divElement.style.backgroundColor = color
        // })
    }
})

