        if (opts.callback)
          opts.callback(data);
      }
      frappe.request.cleanup(opts, data);
      if (opts.always) {
        opts.always(data);
      }
      if (data.status_code && data.status_code > 400 && opts.error) {
        opts.error(data);
      }
    }
    publish(event, message) {
      if (this.socket) {
        this.emit(event, message);
      }
