// Write your script here console.log(url)
const domain_name = window.location.origin
let pathname = window.location.pathname
let path = pathname.split('/')[2]
var order_details = ''
console.log(path)
if(path) getOrderDetails()
async function post_Method(api,payload){
    let k = await fetch(domain_name+"/api/method/"+api,{
        "body":JSON.stringify(payload),
        "method":"POST",
        "headers":{
            // "authorization":"token b98429508526de0:ffa25e763ed7c76",
            "Content-Type":"application/json"
        }
    })
    let val = await k.json()

    return val
}

async function getOrderDetails(){
    let resp = await post_Method("builder.api.doc_detail",{"doctype":"Sales Order","name":path})

    if( resp && resp.message){
        order_details = resp.message;
        console.log(order_details)
        detailComponent()
    }
}

function detailComponent(){
    let element = document.getElementById('order-detail')
    let detail = `<div class='d_flex mb-10 breadcrumb'><a href="/orders">Orders</a> <p>></p> <p>${order_details.name}</p></div>
    <div class='d_flex mb-10 date'>${order_details.delivery_date}</div>
    <div class='d_flex s-between mb-10 '><p class="status">${order_details.status}</p> <p class="customer">${order_details.customer_name}</p></div>
    <div class='d_flex s-between mb-10 item-row'><p class="item-col">Item</p> <p class="item-col">Quantity</p> <p class="item-col">Rate</p> <p class="item-col">Amount</p></div>`
    if(order_details.items && order_details.items.length  != 0){
        let items = "<div class='order-items'>"
        order_details.items.map(item=>{
            items += `<div class="d_flex s-between mb-10 item-row"><div class="item-col d_flex"><img class="item-image" src="${domain_name}${item.image}"><p>${item.item_name}</p></div><p class="item-col">${item.qty}</p><p class="item-col">${item.rate}</p><p class="item-col">${item.amount}</p></div>`
        })
        items += '</div>'
         detail+=items
    }
    detail+=`<div class="d_flex mb-10 total"><p class="item-col"></p><p class="item-col"></p><p class="item-col">Grand Total</p> <p class="item-col">${order_details.total}</p></div>`
    element.innerHTML = detail
}

