async function apiCall(api,payload){
    let k = await fetch(domain_url+"/api/method/"+api,{
        "body":JSON.stringify(payload),
        "method":"POST",
        "headers":{
            "Content-Type":"application/json"
        }
    });
    let val = await k.json();
    return val;
}

async function show_shopping_cart(right_nav_type = 'cart') {
    

    $('body').css('overflow','hidden');
    $("#cart-popup").show();
    let load = document.createElement('div');
    load.id = 'cart_loader';
    load.style = "width: 100%;";
    $("#right-cart-container").append(load);
    [1,2,3,4,5,6,7,8].map(h=>{$("#cart_loader").append(loader)});
    let resp = await apiCall('go1_webshop.go1_webshop.doctype.erp_settings.erp_settings.get_cart_items',{});
    let wishlist = await apiCall('go1_webshop.go1_webshop.doctype.erp_settings.erp_settings.wishlist_items',{});
    wishlist_items = wishlist.message
    let cart_items = resp.message ? resp.message.doc.items : [] ;
    $("#cart_loader").hide();
    $("#right-wish-count").text(wishlist_items.length);
    $("#side-cart-count").text(getCookie('cart_count') ? getCookie('cart_count') : cart_items.length);
    let cart_div = document.createElement('div');
    cart_div.id = 'side-cart';
    cart_div.style = "width: 100%;";
    $("#right-cart-container").append(cart_div);
    if(cart_items.length == 0){
            $("#side-cart").append(no_item);
    }else{
        cart_items.map(cart=>{
            let cart_item_html = `<div class="cart-item img-info d_flex w-full">
                <img class="frappe-builder-613fea2d" src="${cart.image ? window.location.origin+cart.image : '/files/gf_no_image.png' } ">
                <div class="flex-1"  id="item-info-div">
                    <div class="item_name ">${cart.item_name}</div>
                    <div class="item_code " id="item_id">${cart.item_code}</div>
                    <div class="d_flex " style="justify-content: space-between;">
                        <div id="item-rate">${cart.price_list_rate}</div>
                        <div class="d_flex">
                            <div class="d_flex">
