function wishHideButton(){
   var wish = $('#right-wish-count').text();
    var wishDivStyle = $('#side-wish-list').css('display')

    if (wish !== "0" && wishDivStyle == "block") {
        console.log("move show 2")
        $("#checkout-button").hide();
        $("#move-cart-button").show();
    } else {
        console.log("movehide 2")
        $("#checkout-button","#move-cart-button").hide();
    }
}
function cartHideButton(){
     var cart = $('#side-cart-count').text();
    var cartDivStyle = $('#side-cart').css('display')
    if (cart !== "0" && cartDivStyle == "block") {
        $("#checkout-button").show();
        console.log("check show 1")
        $("#move-cart-button").hide();
    } else {
        console.log("check Hide 1")
        $("#checkout-button, #move-cart-button").hide();
    }
    
}
function hide_shopping_cart(event) {
    $("#cart-popup").hide();
    $(".pb-60").attr("style","overflow:auto !important;");

}

function toggle_right_nav(type) {
    if(type == 'cart'){
        $('#right-cart').addClass('active');
        $('#right-wishlist').removeClass('active');
        $('#side-wish-list').hide();
        $('#side-cart').show();
        cartHideButton()
      $('#move-cart-button').hide()
    }else{
         $('#right-wishlist').addClass('active');
        $('#right-cart').removeClass('active');
        $('#side-wish-list').show();
        $('#side-cart').hide();
        wishHideButton()
         $('#checkout-button').hide()
    }
}

// cart update functions  are here

async function update_quantity(event,func){
    let parent = event.parentNode.parentNode.parentNode;
     if(func != 'delete'){
                parent=parent.parentNode;
            }
    let cur_qty = event.parentNode.querySelector('#qty').textContent;
    let qty = func == 'delete' ? 0 : func == 'add' ? Math.round(cur_qty) + 1 : Math.round(cur_qty) - 1;
    let itemElement = parent.querySelector('#item_id').textContent;
    let res = await updateCartItem(qty,itemElement);
    if (res){
        if(qty==0){
            if(parent.id && parent.id == 'item-info-div')  { parent.parentNode.remove() }
            else parent.remove();
            checkCartItems();
        }
        event.parentNode.querySelector('#qty').textContent = qty;
        updateHeaderCartCount(func == 'delete' ? - Math.round(cur_qty) : func == 'add' ? 1 : -1);
        showPopup('success',"Updated Seccessfully");
    }
    cartHideButton()
}

async function updateCartItem(qty,item_id){
    overlay = document.getElementById("overlay");
    overlay.classList.add("block");
    let val = await apiCall("webshop.webshop.shopping_cart.cart.update_cart",{
            "with_items": 1,
            "qty": qty,
            "item_code": item_id
        })
    overlay.classList.remove("block");
    return val.message ?  val.message : null;
}

 function updateHeaderCartCount(val){ 
    let actual_qty = Math.round(document.getElementById("header-cart-count").textContent) + val
    $(".header-cart-count").text (actual_qty);
    $(".mobile-header-cart-count").text(actual_qty);
    $("#side-cart-count").text(actual_qty);
    if(actual_qty == 0){
        $("#side-cart").append(no_item);
    }
}

 async function removeWishlistItem(event){
    let item_div = event.parentNode.parentNode.parentNode.parentNode;
    let item_id = item_div.querySelector('#item_id').textContent;
    removeWishItem(item_id,item_div)
}
async function addItemToCart(event){
    let item_div = event.parentNode.parentNode.parentNode.parentNode;
    let item_id = item_div.querySelector('#item_id').textContent;
     let add = await apiCall('webshop.webshop.shopping_cart.cart.update_cart',{"item_code":item_id,"qty":1});
     if (add.message){
        removeWishItem(item_id,item_div)
     }   
}

async function removeWishItem(item_id,item_div){
    await apiCall('webshop.webshop.doctype.wishlist.wishlist.remove_from_wishlist',{"item_code":item_id});
    $('#right-wish-count').text(Math.round( $('#right-wish-count').text() - 1));
    if(item_div) item_div.parentNode.parentNode.classList.add('hide');
    if(Math.round( $('#right-wish-count').text()) == 0) $("#side-wish-list").append(no_item);
    var wish = $('#right-wish-count').text();
    if (wish == "0") {
    $('#move-cart-button').hide()
    }
}

async function addAllItemsToCart(event){
    //  let add = apiCall('webshop.webshop.shopping_cart.cart.update_cart',{"item_code":'',"qty":1});
    if (wishlist_items && wishlist_items.length != 0){
        wishlist_items.map(async wish=>{
           await removeWishItem(wish.item_code,"")
        })
        let elements = $('#side-wish-list').children();
        for(let k=0;k<elements.length;k++){
            elements[k].remove()
        }
    } 
}

function getCookie(cname) {
  let name = cname + "=";
  let decodedCookie = decodeURIComponent(document.cookie);
  let ca = decodedCookie.split(';');
  for(let i = 0; i < ca.length; i++) {
    let c = ca[i];
    while (c.charAt(0) == ' ') {
      c = c.substring(1);
    }
    if (c.indexOf(name) == 0) {
      return c.substring(name.length, c.length);
    }
  }
  return "";
}

function toggleModal(id,method) {
    if(method == 'open'){
         document.getElementById(id).classList.add('block','active');
    }else{
         document.getElementById(id).classList.remove('block','active');
    }
}

